// === frontend/src/pages/Preferences.js ===
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
imp

function Preferences() {
  const navigate = useNavigate();
  const [prefs, setPrefs] = useState({
    safety: 5,
    affordability: 5,
    social: 5,
    greenSpaces: 5
  });

  const handleChange = (e) => {
    setPrefs({ ...prefs, [e.target.name]: parseInt(e.target.value) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const payload = JSON.parse(atob(token.split('.')[1]));
    await axios.post('http://localhost:5001/api/match/submit', { userId: payload.id, ...prefs });
    const res = await axios.post('http://localhost:5001/api/match/result', prefs);
    localStorage.setItem('results', JSON.stringify(res.data));
    navigate('/results');
  };

  return (
    <div className="preferences-wrapper">
      <form onSubmit={handleSubmit} className="preferences-form">
        <h2>üéØ Rate Your Preferences</h2>
        <p className="form-subtext">(1 = Lowest priority, 10 = Highest priority)</p>

        {Object.entries(prefs).map(([key, value]) => (
          <div key={key} className="form-group">
            <label htmlFor={key}>{key.charAt(0).toUpperCase() + key.slice(1)}</label>
            <input
              type="range"
              name={key}
              id={key}
              min="1"
              max="10"
              value={value}
              onChange={handleChange}
            />
            <span className="score-preview">‚≠ê {value}</span>
          </div>
        ))}

        <button type="submit">Find My Neighborhood</button>
      </form>
    </div>
  );
}

export default Preferences;
